components.customDefaultValue:
try {
  var currentValue = value || {
  "dateFormat": "YYYY-MM-DD",
  "inspections_count": 0,
  "hasValue": (value) => {
    if(value !== undefined && value !== "") return value;
    else return false;
  },
  "addEventListener": (element,eventName, handler) =>{
    if(element) {
      var events = element.getAttribute('events') || [];
      if(!events.includes(eventName)) {
        element.addEventListener(eventName, handler);
          events.push(eventName);
        element.setAttribute('events',events);
      }
}
  }
};
value = currentValue;
}catch(e) {
  console.error('Error from onload: ',e.message);
}

try {
    var url = data.reportapiurl || localStorage.getItem('customApiUrl').split(',')[0];
    var element = document.querySelector("[name='data[inspections.project_id]']");
    if (data.onload && data.onload !== "") {
        data.onload.addEventListener(element, 'change', (event) => {
            var projectId = data.inspections.project_id;
            fetch(url + 'projects/' + projectId).then(response => response.json())
                .then(data => {
                    if (data) {
                        try {
                            var projectData;
                            utils.findComponent(instance.root.components, 'projectdata', ['projectdata'],
                                (components) => projectData = components)
                                projectData.setValue(data);
                        } catch (e) { console.log(e.message) };
                    }
                })
                .catch(error => {
                    console.error('Error fetching project with id ' + projectId + ': ' + error.message);
                });
        });
    }
} catch (error) {
    console.error('Error from default value of project : ' + error.message);
}

templates.tableRow:
{% util.eachComponent(components, function(component) { %}
    {% if (!component.hasOwnProperty('tableView') || component.tableView) { %}
      <td class="editgrid-table-column">
        {{ getView(component, row[component.key]) }}
      </td>
    {% } %}
  {% }) %}
  {% if (!instance.options.readOnly && !instance.disabled) { %}
    <td class="editgrid-table-column">
      <div class="btn-group">
        <button class="btn btn-default btn-light btn-sm editRow" aria-label="{{ t('Edit row') }}"><i class="{{ iconClass('edit') }}"></i></button>
        {% if (!instance.hasRemoveButtons || instance.hasRemoveButtons()) { %}
        <button class="btn btn-danger btn-sm removeRow" aria-label="{{ t('Remove row') }}"><i class="{{ iconClass('trash') }}"></i></button>
        {% } %}
      </div>
    </td>
  {% } %}

